import{_ as e,e as n}from"./app.b397120f.js";var i="/assets/Abb1.03ad073a.png";const r={},t=n('<div class="custom-container danger"><p class="custom-container-title">Achtung</p><p>Die Seite wird gerade noch \xFCberarbeitet. Bei auftretenden Problemen <a href="mailto:florian.langhanki@uni-wuerzburg.de">kontaktieren</a> Sie uns bitte.</p></div><h2 id="_1-ocr4all" tabindex="-1"><a class="header-anchor" href="#_1-ocr4all" aria-hidden="true">#</a> 1. OCR4all</h2><h3 id="_1-1einfuhrung" tabindex="-1"><a class="header-anchor" href="#_1-1einfuhrung" aria-hidden="true">#</a> 1.1 Einf\xFChrung</h3><p style="text-align:justify;">OCR4all ist eine Software, die zur digitalen Texterschlie\xDFung vornehmlich sehr fr\xFCh gedruckter Werke entwickelt wurde, deren aufwendige Drucktypen und oft uneinheitliche Layoutkonzeption die Erkennungsm\xF6glichkeiten vieler anderer Texterkennungsprogramme \xFCbersteigen. Verst\xE4ndlich und selbstst\xE4ndig anwendbar spricht der in OCR4all vorgeschlagene Workflow unter anderem auch einen dezidiert nicht-informatisch vorgebildeten Nutzerkreis an und kombiniert unterschiedliche Arbeitswerkzeuge und Tools innerhalb einer einheitlichen Benutzeroberfl\xE4che. Der st\xE4ndige Wechsel zwischen unterschiedlichen Programmen ist auf diese Weise nicht mehr n\xF6tig. Von der Vorverarbeitung der zur bearbeitenden Bilddateien (sog. Preprocessing) \xFCber die Layoutsegmentierung (sog. Region Segmentation mit LAREX), die Extrahierung der klassifizierten Layoutregionen und Zeilensegmentierung (Line Segmentation) und Texterkennung (Recognition) bis hin zur Korrektur der erkannten Texte (Ground Truth Production) und der Erstellung werkspezifischer OCR-Modelle in einem Trainingsmodul beschreibt OCR4all einen vollwertigen OCR-Workflow (s. Abb. 1).</p><p><img src="'+i+'" alt="Abb1.png"></p><p style="align-text:center;">Abb. 1: Hauptkomponenten des OCR4all-Workflows.</p><p style="text-align:justify;">V. a. durch die M\xF6glichkeit der Herstellung und des Trainings werkspezifischer Texterkennungsmodelle, die sich dann theoretisch immer auch auf andere Drucke anwenden lassen, k\xF6nnen mit OCR4all bei so gut wie allen gedruckten Texten sehr gute Ergebnisse in der digitalen Texterschlie\xDFung erzielt werden. Mit Blick auf diese Gesamtkonzeption gibt die vorliegende Anleitung einen ausf\xFChrlichen und detaillierten Einblick in die Arbeit und Einsatzm\xF6glichkeiten von OCR4all im Rahmen der OCR besonders fr\xFCher Drucke. W\xE4hrend in Kapitel 1 die allgemeine Einrichtung und Ordnerstruktur erl\xE4utert wird, behandelt Kapitel 2 einen empfohlenen Vorverarbeitungsschritt von Scans und Bilddateien au\xDFerhalb von OCR4all, der zu einer Verbesserung von Ergebnissen und Erleichterung der Arbeitsschritte innerhalb von OCR4all f\xFChrt. Kapitel 3 behandelt das Starten von OCR4all sowie eine \xDCbersicht der grundlegenden Funktionen. Das daran anschlie\xDFende Kapitel 4 f\xFChrt dann detailliert durch die unterschiedlichen und aufeinander aufbauenden Teilmodule des oben beschriebenen OCR- Workflows und stellt die eigentliche Bearbeitung von Drucken und Erstellung von OCR-Texten praktisch vor. Das abschlie\xDFende Kapitel 5 widmet sich den aktuell h\xE4ufigsten Benutzerproblemen.</p>',7);function a(l,d){return t}var u=e(r,[["render",a],["__file","1.1_Introduction.html.vue"]]);export{u as default};
